import React from 'react';
import { TYPE } from 'theme';
import styled from 'styled-components';
import { ReactComponent as SwitchIcon } from 'assets/svg/switch.svg';

const BuyInputs = (props: any) => {
  return (
    <InputsContainer>
      <div>
        <AmountInputLabel>Send</AmountInputLabel>
        <AmountInput />
      </div>
      <SwitchIconWrapper>
        <SwitchIcon />
      </SwitchIconWrapper>
      <div>
        <AmountInputLabel>Receive</AmountInputLabel>
        <AmountInput disabled />
      </div>
    </InputsContainer>
  );
};

const InputsContainer = styled.div`
  margin: 14px 0;
`;

const AmountInputLabel = styled(TYPE.caption)`
  padding-left: 8px;
  padding-bottom: 4px;
  display: block;
`;

const AmountInput = styled.input`
  height: 72px;
  width: -webkit-fill-available;
  border: 1px solid #e6e6e6;
  box-sizing: border-box;
  border-radius: 12px;
`;

const SwitchIconWrapper = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 8px;
  margin-bottom: -8px;
`

export default BuyInputs;
