import "./Header.css";
import React from 'react';
import { NavLink } from 'react-router-dom';
import { Roles } from '../../../constants/roles';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../../app/store';
import { logout } from '../../../app/authSlice';
import AuthService from '../../../services/auth.service';

function Header() {
  const { isLoggedIn, user } = useSelector((state: RootState) => state.auth);
  const dispatch = useDispatch();

  const logOutUser = () => {
    dispatch(logout());
    AuthService.logout();
  };

  return (
    <header className='main-header'>
      <nav className='navigation'>
        <div className="main-header__logo">
          <span className='logo__company-name'>Titanium</span>
        </div>
        <div className="main-header__menu">
          <ul className='menu-list'>
            <li className='menu-list__item'>
              <NavLink exact to="/" className='menu-list__link'>
                Home
              </NavLink>
            </li>
            {isLoggedIn ? (
              <>
                <li className='menu-list__item'>
                  <NavLink to="/profile" className='menu-list__link'>Profile</NavLink>
                </li>
                {user?.role === Roles.ADMIN && (
                  <li className='menu-list__item'>
                    <NavLink to="/admin" className='menu-list__link'>Admin</NavLink>
                  </li>
                )}
                <li className='menu-list__item'>
                  <a href="#" className='menu-list__link' onClick={logOutUser}>
                    Logout
                  </a>
                </li>
              </>
            ) : (
              <>
                <li className='menu-list__item'>
                  <NavLink to="/login" className='menu-list__link'>Login</NavLink>
                </li>
                <li className='menu-list__item'>
                  <NavLink to="/register" className='menu-list__link'>Register</NavLink>
                </li>
              </>
            )}
          </ul>
        </div>
      </nav>
    </header>
  );
}

export { Header };
