import styled from 'styled-components'
import Button from 'components/Views/Button'
import MetaMaskIcon from 'assets/png/metamask.png'
import CoinbaseIcon from 'assets/png/coinbase.png'

export const WalletButtons = (props: any) => {
  return (
    <WalletButtonsContainer>
      <WalletButton>
        <WalletName>Metamask</WalletName>
        <IconWrapper>
          <img src={MetaMaskIcon} alt="MetaMask icon" />
        </IconWrapper>
      </WalletButton>
      <WalletButton>
        <WalletName>Coinbase Wallet</WalletName>
        <IconWrapper>
          <img src={CoinbaseIcon} alt="Coinbase wallet icon" />
        </IconWrapper>
      </WalletButton>
    </WalletButtonsContainer>
  )
}

const WalletButtonsContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 30px;
`

const WalletButton = styled(Button)`
  margin-bottom: 20px;
  background: linear-gradient(0deg, rgba(221, 221, 221, 0.2), rgba(221, 221, 221, 0.2));
  border: 1px solid ${({ theme }) => theme.grey};
  color: ${({ theme }) => theme.black};
  display: flex;
  align-items: center;
  justify-content: center;
`

const IconWrapper = styled.div`
  width: 32px;

  img {
    width: 26px;
    height: 26px;
  }
`
const WalletName = styled.div`
  flex-basis: 100%;
`
