import styled from 'styled-components';

const Button = styled.button`
  appearance: none;
  background-color: ${({ theme }) => theme.accentColor};
  font-size: 16px;
  padding: 4px 12px;
  outline: none;
  border: none;
  color: white;
  cursor: pointer;
  border-radius: 4px;
  transition: all 0.15s;
  line-height: 18.75px;
  width: -webkit-fill-available;
  height: 48px;
  font-weight: 700;
`;

export default Button;
