import React, { useState } from 'react';
import { useModalOpen } from 'state/application/hooks'
import { ApplicationModal } from 'state/application/actions'
import styled from 'styled-components'
import { ReactComponent as CrossIcon } from 'assets/svg/cross.svg'
import { TYPE } from 'theme'
import { useToggleModal } from 'state/application/hooks'
import { Input, Button } from 'components'

export const AddStringModal = (props: any) => {
  const isOpen = useModalOpen(ApplicationModal.ADD_STRING)
  const toggleModal = useToggleModal(ApplicationModal.ADD_STRING)
  const [error, setError] = useState(true)


  return (
    <>
      {isOpen && (
        <ModalOverlay>
          <ModalContainer>
            <CrossIconStyled onClick={toggleModal}/>
            <TYPE.H4>Add new string</TYPE.H4>
            <Input type="text" name="name" onInput={onUserInput}/>
            <Button disabled={error}>Add string</Button>
          </ModalContainer>
        </ModalOverlay>
      )}
    </>
  )
}

const CrossIconStyled = styled(CrossIcon)`
  float: right;
  margin-top: -5%;
  margin-right: -5%;
  cursor: pointer;
`

const ModalContainer = styled.div`
  background-color: white;
  width: 483px;
  height: auto;
  padding: 29px 31px 41px 31px;
  box-sizing: border-box;
  left: 0;
  top: 0;
  bottom: 0;
  right: 0;
  margin: auto;
  margin-top: 20vh;
  border-radius: 4px;
  text-align: left;
`

const ModalOverlay = styled.div`
  position: absolute;
  background-color: rgba(41, 47, 48, 0.7);
  width: 100vw;
  height: 100vh;
  left: 0;
  top: 0;
`
