import './Profile.css';
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../app/store';
import ChangePassword from '../../components/Controllers/ChangePassword/ChangePassword';
import AuthService from '../../services/auth.service';
import { logout } from '../../app/authSlice';
import styled from 'styled-components';
import { history } from '../../helpers/history';

const DeleteMyAccountButton = styled.button`
  cursor: pointer;
  display: inline-block;
  font-weight: 400;
  text-align: center;
  vertical-align: middle;
  -webkit-user-select: none;
  -ms-user-select: none;
  user-select: none;
  border: 1px solid transparent;
  padding: 0.375rem 0.75rem;
  font-size: 1rem;
  line-height: 1.5;
  border-radius: 0.25rem;
  transition: color 0.15s ease-in-out, background-color 0.15s ease-in-out, border-color 0.15s ease-in-out,
    box-shadow 0.15s ease-in-out;
  color: #fff;
  background-color: #dc3545;
  border-color: #dc3545;
  margin-top: 50px;

  &:hover {
    color: #fff;
    background-color: #c82333;
    border-color: #bd2130;
  }
`;

const Profile = () => {
  const [deleteAccountButtonText, setDeleteAccountButtonText] = useState('Delete my account');
  const [deleteAccountConfirmed, setDeleteAccountConfirmed] = useState(false);
  const { user } = useSelector((state: RootState) => state.auth);
  const dispatch = useDispatch();

  const deleteMyAccount = () => {
    if (deleteAccountConfirmed) {
      AuthService.delete().then(() => {
        dispatch(logout());
        AuthService.logout();
      });
    } else {
      setDeleteAccountButtonText('Are you sure you want to delete your account');
      setDeleteAccountConfirmed(true);
    }
  };

  return (
    <div className="profile">
      Profile
      {user ? (
        <>
          <p>id: {user.id}</p>
          <p>email: {user.email}</p>
          <p>role: {user.role}</p>
          <div className="profile-actions">
            <ChangePassword />
            <DeleteMyAccountButton onClick={deleteMyAccount}>{deleteAccountButtonText}</DeleteMyAccountButton>
          </div>
        </>
      ) : (
        false
      )}
    </div>
  );
};

export default Profile;
