import React, { useState, useEffect } from "react";
import './Home.css'
import styled from 'styled-components'
import { StringsList, SearchInput } from 'components'
import { AddStringModal } from 'components/Views/Modals/AddString'

const Home = () => {
  const [stringList, setStringList] = useState([""]);
  const [searchResult, setSearchResult] = useState(['']);

  async function updateStringList() {
    const response = await fetch("http://localhost:3001/");
    const parsedData = await response.json();
    setStringList(parsedData.data);
  }

  useEffect(() => {
    updateStringList();
  }, []);

  return (
    <>
      <MainContainer>
        <SearchInput setSearchResult={setSearchResult}></SearchInput>
        <StringsList stringList={searchResult} listTitle="Search results:" theme="green"></StringsList>
        <br/><br />
        <StringsList stringList={stringList} listTitle="All strings:" ></StringsList>
      </MainContainer>
      <AddStringModal updateStringList={updateStringList}/>
    </>
  )
}

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

export default Home
