import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { decrement, increment, incrementByAmount, incrementAsync, selectCount } from './counterSlice';
import BaseButton from '../../components/Views/Button/Button';
import styled from 'styled-components';

const Row = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;

  &:not(:last-child) {
    margin-bottom: 16px;
  }
`;

const Button = styled(BaseButton)`
  &:hover,
  &:focus {
    border: 2px solid rgba(112, 76, 182, 0.4);
  }

  &:active {
    background-color: rgba(112, 76, 182, 0.2);
  }
`;

const AsyncButton = styled(BaseButton)`
  position: relative;
  margin-left: 8px;

  &:after {
    content: '';
    background-color: rgba(112, 76, 182, 0.15);
    display: block;
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    opacity: 0;
    transition: width 1s linear, opacity 0.5s ease 1s;
  }

  &:active:after {
    width: 0%;
    opacity: 1;
    transition: 0s;
  }
`;

const Value = styled.span`
  font-size: 60px;
  padding-left: 16px;
  padding-right: 16px;
  margin-top: 2px;
  font-family: 'Courier New', Courier, monospace;
`;

const Textbox = styled.input`
  font-size: 22px;
  padding: 2px;
  width: 64px;
  text-align: center;
  margin-right: 8px;
`;

export function Counter() {
  const count = useSelector(selectCount);
  const dispatch = useDispatch();
  const [incrementAmount, setIncrementAmount] = useState('2');

  return (
    <div>
      <Row>
        <Button aria-label="Increment value" onClick={() => dispatch(increment())}>
          +
        </Button>
        <Value>{count}</Value>
        <Button aria-label="Decrement value" onClick={() => dispatch(decrement())}>
          -
        </Button>
      </Row>
      <Row>
        <Textbox
          aria-label="Set increment amount"
          value={incrementAmount}
          onChange={(e) => setIncrementAmount(e.target.value)}
        />
        <Button onClick={() => dispatch(incrementByAmount(Number(incrementAmount) || 0))}>Add Amount</Button>
        <AsyncButton onClick={() => dispatch(incrementAsync(Number(incrementAmount) || 0))}>Add Async</AsyncButton>
      </Row>
    </div>
  );
}
