import React from "react";
import styled from "styled-components";
import { TYPE } from "theme";
import Item  from 'dbo/item'

interface Props {
  listTitle: string;
  stringList: Item[];
}

function showItems(list: Item[]) {
  if (list.length === 0) {
    return "Nothing found, please enter another keyword";
  } else {
    return list.map((item: Item) => <ListItem> {item.text} </ListItem>);
  }
}

function showSearchResults(list: Item[]) {
  if (list.length === 0) {
    return "Nothing found, please enter another keyword";
  } else {
    return list.map((item: Item) => (
      <SearchResultListItem> {item.text} </SearchResultListItem>
    ));
  }
}

export const StringsList: React.FC<Props> = ({ stringList, listTitle }) => {
  return (
    <MainContainer>
      <TYPE.H4>{listTitle}</TYPE.H4>
      <ListContainer>{showItems(stringList)}</ListContainer>
    </MainContainer>
  );
};

export const SearchResult: React.FC<Props> = ({ stringList, listTitle }) => {
  return (
    <MainContainer>
      <TYPE.H4>{listTitle}</TYPE.H4>
      <ListContainer>{showSearchResults(stringList)}</ListContainer>
    </MainContainer>
  );
};

const MainContainer = styled.div`
  text-align: left;
  width: 80%;
`;

const ListContainer = styled.ul`
  list-style: none;
  padding: 0;
`;

const ListItem = styled.ul`
  border: 1px solid #e6e6e6;
  border-radius: 12px;
  margin: 1em 0;
  padding: 1em 1em;
`;

const SearchResultListItem = styled(ListItem)`
  border-color: #aae3aa;
  background-color: #f3fff3;
`;
