import React, { useState } from 'react'
import { TYPE } from 'theme'
import styled from 'styled-components'
import { ReactComponent as SwitchIcon } from 'assets/svg/switch.svg'
import CurrencySwitcher from './CurrencySwitcher'

interface Props {
  label: string
  showBalance?: boolean
  disabled?: boolean
  typedValue: string
  onUserInput: (event: any) => void
}

const CurrencyInput = ({ label, showBalance, disabled, onUserInput, typedValue }: Props) => {
  return (
    <div>
      <AmountInputLabel>{label}</AmountInputLabel>
      <CurrencyInputPanel>
        <RowSpaceBetween>
          <CurrencySwitcher />
          <AmountInput placeholder="0.0" type="tel" pattern="[0-9]*" onInput={onUserInput} value={typedValue} disabled={disabled}/>
        </RowSpaceBetween>
        {showBalance && (
          <Balance>
            Balance: <span style={{ fontWeight: 700 }}>000</span>
          </Balance>
        )}
      </CurrencyInputPanel>
    </div>
  )
}

const AmountInputLabel = styled(TYPE.caption)`
  padding-left: 8px;
  padding-bottom: 4px;
  display: block;
`

const CurrencyInputPanel = styled.div`
  height: 72px;
  width: -webkit-fill-available;
  border: 1px solid #e6e6e6;
  box-sizing: border-box;
  border-radius: 12px;
  padding: 16px;
`

const AmountInput = styled.input`
  font-size: 22px;
  line-height: 30px;
  text-align: right;
  font-weight: 800;
  color: ${({ theme }) => theme.black};
  width: -webkit-fill-available;
  border: none;
  outline: none;
  &:disabled {
    background-color: white;
  }
`

const Balance = styled.div`
  color: ${({ theme }) => theme.greyCapture};
  font-size: 12px;
  line-height: 16px;
  font-weight: 400;
`

const RowSpaceBetween = styled.div`
  display: flex;
  justify-content: space-between;
`

export default CurrencyInput
