import styled from 'styled-components';

const BaseButton = styled.button`
  appearance: none;
  background: none;
  font-size: 20px;
  padding: 4px 12px;
  outline: none;
  border: 2px solid transparent;
  color: #0061ff;
  cursor: pointer;
  background-color: rgba(112, 76, 182, 0.1);
  border-radius: 2px;
  transition: all 0.15s;
`;

export default BaseButton;
