import React, { useState } from 'react'
import { TYPE } from 'theme'
import styled from 'styled-components'
import { ReactComponent as SwitchIcon } from 'assets/svg/switch.svg'
import CurrencyInput from './CurrencyInput'

const BuyInputs = (props: any) => {
  const [sendTypedValue, setSendTypedValue] = useState('')
  const [receiveTypedValue, setReceiveTypedValue] = useState('')

  const onUserInput = (event: any) => {
    const cleanedValue = event.target.value.match(/\d{0,}\.?\d{0,4}/)?.[0] || ''
    setSendTypedValue(cleanedValue)
    setReceiveTypedValue((parseFloat(cleanedValue) * 0.4).toString())
  }

  return (
    <InputsContainer>
      <CurrencyInput label="Send" showBalance onUserInput={onUserInput} typedValue={typedValue}/>
      <SwitchIconWrapper>
        <SwitchIcon />
      </SwitchIconWrapper>
      <CurrencyInput label="Receive" disabled onUserInput={onUserInput} typedValue={typedValue}/>
    </InputsContainer>
  )
}

const InputsContainer = styled.div`
  margin: 14px 0;
`

const AmountInputLabel = styled(TYPE.caption)`
  padding-left: 8px;
  padding-bottom: 4px;
  display: block;
`

const AmountInput = styled.input`
  height: 72px;
  width: -webkit-fill-available;
  border: 1px solid #e6e6e6;
  box-sizing: border-box;
  border-radius: 12px;
  font-size: 22px;
  line-height: 30px;
  text-align: right;
  font-weight: 800;
  color: ${({ theme }) => theme.black};
  padding: 16px;
`

const SwitchIconWrapper = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 8px;
  margin-bottom: -8px;
`

export default BuyInputs
